import{_ as t,c as e,o as r,a}from"./app.22d68e97.js";const g=JSON.parse('{"title":"Shopizer product REST api documentation","description":"","frontmatter":{"title":"Shopizer product REST api documentation"},"headers":[{"level":2,"title":"Product characteristics","slug":"product-characteristics"},{"level":2,"title":"Product options","slug":"product-options"},{"level":2,"title":"Product definition","slug":"product-definition"},{"level":2,"title":"Product properties (attributes)","slug":"product-properties-attributes"},{"level":2,"title":"Product variants (options)","slug":"product-variants-options"},{"level":2,"title":"Product instances","slug":"product-instances"},{"level":2,"title":"Product classification","slug":"product-classification"},{"level":2,"title":"List products","slug":"list-products"},{"level":2,"title":"Search products","slug":"search-products"},{"level":2,"title":"Swagger documentations","slug":"swagger-documentations"}],"relativePath":"api/products/index.md"}'),i={name:"api/products/index.md"},o=a('<h1 id="product-api-reference" tabindex="-1">Product API Reference <a class="header-anchor" href="#product-api-reference" aria-hidden="true">#</a></h1><h2 id="product-characteristics" tabindex="-1">Product characteristics <a class="header-anchor" href="#product-characteristics" aria-hidden="true">#</a></h2><p>Products can be configured with characteristics. Characteristics are defined outside of the product and can used during the configuration of different products.</p><ul><li><a href="./product-types.html">Product types</a></li><li><a href="./product-brands.html">Product brands</a></li></ul><h2 id="product-options" tabindex="-1">Product options <a class="header-anchor" href="#product-options" aria-hidden="true">#</a></h2><p>Products can have options. Options are used to differenciate products having the same definition. For instance, a product <strong>color</strong> or a product <strong>size</strong> are options of the same base product. Options are defined outside of the product and can used during the configuration of different products.</p><ul><li><a href="./product-options.html">Product options</a></li></ul><h2 id="product-definition" tabindex="-1">Product definition <a class="header-anchor" href="#product-definition" aria-hidden="true">#</a></h2><p>Product definition is product metadata that defines core non variables elements of a product. For instance a <strong>t-shirt</strong> is a basic product definition. A product definition has no variants attached. It contains basic characteristiques, core attributes and dynamic attributes.</p><ul><li><a href="./product-definition.html">Product definition</a></li><li><a href="./product-images.html">Product images</a></li></ul><h2 id="product-properties-attributes" tabindex="-1">Product properties (attributes) <a class="header-anchor" href="#product-properties-attributes" aria-hidden="true">#</a></h2><p>Properties (attributes) adds extension to the product core attributes by allowing the creation of custom properties (attributes).</p><ul><li><a href="./product-properties.html">Product custom properties</a></li></ul><h2 id="product-variants-options" tabindex="-1">Product variants (options) <a class="header-anchor" href="#product-variants-options" aria-hidden="true">#</a></h2><h2 id="product-instances" tabindex="-1">Product instances <a class="header-anchor" href="#product-instances" aria-hidden="true">#</a></h2><h2 id="product-classification" tabindex="-1">Product classification <a class="header-anchor" href="#product-classification" aria-hidden="true">#</a></h2><h2 id="list-products" tabindex="-1">List products <a class="header-anchor" href="#list-products" aria-hidden="true">#</a></h2><h2 id="search-products" tabindex="-1">Search products <a class="header-anchor" href="#search-products" aria-hidden="true">#</a></h2><h2 id="swagger-documentations" tabindex="-1">Swagger documentations <a class="header-anchor" href="#swagger-documentations" aria-hidden="true">#</a></h2><p>All apis are described on Swaggerhub Shopizer repository</p><p><a href="https://groups.google.com/forum/#!forum/shopizer" target="_blank" rel="noopener noreferrer">Shopize Swagger Documentation</a><a href="https://app.swaggerhub.com/apis-docs/shopizer/shopizer-rest-api/3.0.1" target="_blank" rel="noopener noreferrer">https://app.swaggerhub.com/apis-docs/shopizer/shopizer-rest-api/3.0.1</a></p>',21),s=[o];function c(d,n,u,p,h,l){return r(),e("div",null,s)}var m=t(i,[["render",c]]);export{g as __pageData,m as default};
